INSERT INTO `oc_modification` (`modification_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES ('3', 'Ajax Live Options', 'ajax-live-options-percentage', 'Denise (rei7092@gmail.com)', '2.3.2', 'http://www.everthemes.com/', '<?xml version="1.0" encoding="utf-8"?>\n<modification>\n    <name>Ajax Live Options</name>\n    <code>ajax-live-options-percentage</code>\n    <version>2.3.2</version>\n    <author>Denise (rei7092@gmail.com)</author>\n    <link>http://www.everthemes.com/</link>\n    <file path="catalog/model/catalog/product.php">\n        <operation>\n            <search>\n               <![CDATA[\'weight\'                  => $product_option_value[\'weight\'],]]>\n            </search>\n            <add position="before">\n            <![CDATA[\n            // Ajax Live Options\n            \'points\'        => $product_option_value[\'points\'],\n            \'points_prefix\' => $product_option_value[\'points_prefix\'],\n            ]]>\n            </add>\n        </operation>\n    </file>\n\n    <file path="catalog/view/theme/sellegance/template/product/{product,quickview}.tpl">\n        <operation>\n            <search>\n               <![CDATA[$(\'.cloud-zoom, .cloud-zoom-gallery\').CloudZoom();]]>\n            </search>\n            <add position="after" offset="3">\n            <![CDATA[\n            <script type="text/javascript" src="index.php?route=product/live_options/js&product_id=<?php echo $product_id; ?>"></script>\n            ]]>\n            </add>\n        </operation>\n        <operation>\n            <search>\n               <![CDATA[<?php echo $price; ?>]]>\n            </search>\n            <add position="replace">\n            <![CDATA[\n            <span class="<?php echo $live_options[\'live_options_price_container\']; ?>"><?php echo $price; ?></span>\n            ]]>\n            </add>\n        </operation>\n        <operation>\n            <search>\n               <![CDATA[<?php echo $special; ?>]]>\n            </search>\n            <add position="replace">\n            <![CDATA[\n            <span class="<?php echo $live_options[\'live_options_special_container\']; ?>"><?php echo $special; ?></span>\n            ]]>\n            </add>\n        </operation>\n        <operation>\n            <search>\n               <![CDATA[<?php echo $points; ?>]]>\n            </search>\n            <add position="replace">\n            <![CDATA[\n            <span class="<?php echo $live_options[\'live_options_points_container\']; ?>"><?php echo $points; ?></span>\n            ]]>\n            </add>\n        </operation>\n        <operation>\n            <search>\n               <![CDATA[<?php echo $reward; ?>]]>\n            </search>\n            <add position="replace">\n            <![CDATA[\n            <span class="<?php echo $live_options[\'live_options_reward_container\']; ?>"><?php echo $reward; ?></span>\n            ]]>\n            </add>\n        </operation>\n        <operation>\n            <search>\n               <![CDATA[<?php echo $tax; ?>]]>\n            </search>\n            <add position="replace">\n            <![CDATA[\n            <span class="<?php echo $live_options[\'live_options_tax_container\']; ?>"><?php echo $tax; ?></span>\n            ]]>\n            </add>\n        </operation>\n        \n    </file>\n    <file path="catalog/controller/product/{product,quickview}.php">\n        <operation info="Define CSS calss name">\n            <search><![CDATA[$data[\'products\'] = array();]]></search>\n            <add position="before"><![CDATA[\n            // Ajax Live Options\n            if($this->config->get(\'live_options_ajax_status\')){\n                $data[\'live_options\'] = array(\n                    \'live_options_container\' => substr($this->config->get(\'live_options_container\'), 1),\n                    \'live_options_special_container\' => substr($this->config->get(\'live_options_special_container\'), 1),\n                    \'live_options_price_container\' => substr($this->config->get(\'live_options_price_container\'), 1),\n                    \'live_options_tax_container\' => substr($this->config->get(\'live_options_tax_container\'), 1),\n                    \'live_options_points_container\' => substr($this->config->get(\'live_options_points_container\'), 1),\n                    \'live_options_reward_container\' => substr($this->config->get(\'live_options_reward_container\'), 1),\n                );\n            }\n            ]]></add>\n        </operation>\n        <operation info="Show Full Option Price">\n            <search><![CDATA[foreach ($option[\'product_option_value\'] as $option_value) {]]></search>\n            <add position="after"><![CDATA[\n            // show total amount\n            if($this->config->get(\'live_options_ajax_status\') && !$this->config->get(\'live_options_show_options_type\') && $option_value[\'price\'] > 0){\n                if ($option_value[\'price_prefix\'] == \'-\' && $option_value[\'price\']) {\n                    $option_value[\'price\'] = (($product_info[\'special\'] ? ($product_info[\'special\'] - $option_value[\'price\']) : ($product_info[\'price\']) - $option_value[\'price\']));\n                } elseif ($option_value[\'price_prefix\'] == \'+\' && $option_value[\'price\']) {\n                    $option_value[\'price\'] = (($product_info[\'special\'] ? ($product_info[\'special\'] + $option_value[\'price\']) : ($product_info[\'price\']) + $option_value[\'price\']));\n                }\n                $option_value[\'price_prefix\'] = \'\';\n            }\n            ]]></add>\n        </operation>\n        <operation info="Show Full Option Price">\n            <search><![CDATA[\n                $price = $this->currency->format($this->tax->calculate($option_value[\'price\'], $product_info[\'tax_class_id\'], $this->config->get(\'config_tax\') ? \'P\' : false)\n                ]]></search>\n            <add position="before"><![CDATA[\n                if (version_compare(VERSION, \'2.0.0.0\', \'>=\') && version_compare(VERSION, \'2.2.0.0\', \'<\')) {\n                    $currency_code = false;\n                }\n                elseif(version_compare(VERSION, \'2.2.0.0\', \'>=\') && version_compare(VERSION, \'2.4.0.0\', \'<\')){\n                    $currency_code = $this->session->data[\'currency\'];\n                }\n                // show total amount\n                if($this->config->get(\'live_options_ajax_status\') && !$this->config->get(\'live_options_show_options_type\')){\n                   $price = $this->currency->format($this->tax->calculate($option_value[\'price\'], $product_info[\'tax_class_id\'], $this->config->get(\'config_tax\')), $currency_code);\n                }\n                else\n            ]]></add>\n        </operation>\n    </file>\n</modification>\n', '1', '2017-07-20 21:09:03');
INSERT INTO `oc_modification` (`modification_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES ('4', 'AJAX Live Search', 'ajax-live-search', 'Denise (rei7092@gmail.com)', '2.2.4', 'http://demo.j-mall.com.tw/', '<?xml version="1.0" encoding="utf-8"?>\r\n<modification>\r\n    <name>AJAX Live Search</name>\r\n    <code>ajax-live-search</code>\r\n    <version>2.2.4</version>\r\n    <author>Denise (rei7092@gmail.com)</author>\r\n    <link>http://demo.j-mall.com.tw/</link>\r\n    <file path="catalog/controller/common/footer.php">\r\n        <operation>\r\n            <search>\r\n               <![CDATA[$this->load->language(\'common/footer\');]]>\r\n            </search>\r\n            <add position="before"><![CDATA[\r\n            \t$this->load->language(\'product/search\');\r\n                $data[\'text_empty\'] = $this->language->get(\'text_empty\');\r\n\r\n            \t$data[\'text_view_all_results\'] = $this->config->get(\'live_search_view_all_results\')[$this->config->get(\'config_language_id\')][\'name\'];\r\n                $data[\'live_search_ajax_status\'] = $this->config->get(\'live_search_ajax_status\');\r\n              $data[\'live_search_show_image\'] = $this->config->get(\'live_search_show_image\');\r\n              $data[\'live_search_show_price\'] = $this->config->get(\'live_search_show_price\');\r\n              $data[\'live_search_show_description\'] = $this->config->get(\'live_search_show_description\');\r\n              $data[\'live_search_href\'] = $this->url->link(\'product/search\', \'search=\');\r\n              $data[\'live_search_min_length\'] = $this->config->get(\'live_search_min_length\');\r\n            ]]>\r\n            </add>\r\n        </operation>\r\n    </file>\r\n    <file path="catalog/view/theme/*/template/common/footer.tpl">\r\n        <operation>\r\n            <search>\r\n               <![CDATA[</footer>]]>\r\n            </search>\r\n            <add position="after">\r\n            <![CDATA[\r\n            <?php if ($live_search_ajax_status):?>\r\n\t            <link rel="stylesheet" type="text/css" href="catalog/view/theme/default/stylesheet/live_search.css" />\r\n\t\t\t\t<script type="text/javascript"><!--\r\n\t\t\t\t\tvar live_search = {\r\n\t\t\t\t\t\tselector: \'#search input[name=\\\'search\\\']\',\r\n\t\t\t\t\t\ttext_no_matches: \'<?php echo $text_empty; ?>\',\r\n\t\t\t\t\t\theight: \'50px\'\r\n\t\t\t\t\t}\r\n\r\n\t\t\t\t\t$(document).ready(function() {\r\n\t\t\t\t\t\tvar html = \'\';\r\n\t\t\t\t\t\thtml += \'<div class="live-search">\';\r\n\t\t\t\t\t\thtml += \'\t<ul>\';\r\n\t\t\t\t\t\thtml += \'\t</ul>\';\r\n\t\t\t\t\t\thtml += \'<div class="result-text"></div>\';\r\n\t\t\t\t\t\thtml += \'</div>\';\r\n\r\n\t\t\t\t\t\t//$(live_search.selector).parent().closest(\'div\').after(html);\r\n\t\t\t\t\t\t$(live_search.selector).after(html);\r\n\r\n\t\t\t\t\t\t$(live_search.selector).autocomplete({\r\n\t\t\t\t\t\t\t\'source\': function(request, response) {\r\n\t\t\t\t\t\t\t\tvar filter_name = $(live_search.selector).val();\r\n\t\t\t\t\t\t\t\tvar live_search_min_length = \'<?php echo (int)$live_search_min_length; ?>\';\r\n\t\t\t\t\t\t\t\tif (filter_name.length < live_search_min_length) {\r\n\t\t\t\t\t\t\t\t\t$(\'.live-search\').css(\'display\',\'none\');\r\n\t\t\t\t\t\t\t\t}\r\n\t\t\t\t\t\t\t\telse{\r\n\t\t\t\t\t\t\t\t\tvar html = \'\';\r\n\t\t\t\t\t\t\t\t\thtml += \'<li style="text-align: center;height:10px;">\';\r\n\t\t\t\t\t\t\t\t\thtml +=\t\'<img class="loading" src="catalog/view/theme/default/image/loading.gif" />\';\r\n\t\t\t\t\t\t\t\t\thtml +=\t\'</li>\';\r\n\t\t\t\t\t\t\t\t\t$(\'.live-search ul\').html(html);\r\n\t\t\t\t\t\t\t\t\t$(\'.live-search\').css(\'display\',\'block\');\r\n\r\n\t\t\t\t\t\t\t\t\t$.ajax({\r\n\t\t\t\t\t\t\t\t\t\turl: \'index.php?route=product/live_search&filter_name=\' +  encodeURIComponent(filter_name),\r\n\t\t\t\t\t\t\t\t\t\tdataType: \'json\',\r\n\t\t\t\t\t\t\t\t\t\tsuccess: function(result) {\r\n\t\t\t\t\t\t\t\t\t\t\tvar products = result.products;\r\n\t\t\t\t\t\t\t\t\t\t\t$(\'.live-search ul li\').remove();\r\n\t\t\t\t\t\t\t\t\t\t\t$(\'.result-text\').html(\'\');\r\n\t\t\t\t\t\t\t\t\t\t\tif (!$.isEmptyObject(products)) {\r\n\t\t\t\t\t\t\t\t\t\t\t\tvar show_image = <?php echo $live_search_show_image;?>;\r\n\t\t\t\t\t\t\t\t\t\t\t\tvar show_price = <?php echo $live_search_show_price;?>;\r\n\t\t\t\t\t\t\t\t\t\t\t\tvar show_description = <?php echo $live_search_show_description;?>;\r\n\t\t\t\t\t\t\t\t\t\t\t\t$(\'.result-text\').html(\'<a href="<?php echo $live_search_href;?>\'+filter_name+\'" class="view-all-results"><?php echo $text_view_all_results;?> (\'+result.total+\')</a>\');\r\n\r\n\t\t\t\t\t\t\t\t\t\t\t\t$.each(products, function(index,product) {\r\n\t\t\t\t\t\t\t\t\t\t\t\t\tvar html = \'\';\r\n\t\t\t\t\t\t\t\t\t\t\t\t\t\r\n\t\t\t\t\t\t\t\t\t\t\t\t\thtml += \'<li>\';\r\n\t\t\t\t\t\t\t\t\t\t\t\t\thtml += \'<a href="\' + product.url + \'" title="\' + product.name + \'">\';\r\n\t\t\t\t\t\t\t\t\t\t\t\t\tif(product.image && show_image){\r\n\t\t\t\t\t\t\t\t\t\t\t\t\t\thtml += \'\t<div class="product-image"><img alt="\' + product.name + \'" src="\' + product.image + \'"></div>\';\r\n\t\t\t\t\t\t\t\t\t\t\t\t\t}\r\n\t\t\t\t\t\t\t\t\t\t\t\t\thtml += \'\t<div class="product-name">\' + product.name ;\r\n\t\t\t\t\t\t\t\t\t\t\t\t\tif(show_description){\r\n\t\t\t\t\t\t\t\t\t\t\t\t\t\thtml += \'<p>\' + product.extra_info + \'</p>\';\r\n\t\t\t\t\t\t\t\t\t\t\t\t\t}\r\n\t\t\t\t\t\t\t\t\t\t\t\t\thtml += \'</div>\';\r\n\t\t\t\t\t\t\t\t\t\t\t\t\tif(show_price){\r\n\t\t\t\t\t\t\t\t\t\t\t\t\t\tif (product.special) {\r\n\t\t\t\t\t\t\t\t\t\t\t\t\t\t\thtml += \'\t<div class="product-price"><span class="special">\' + product.price + \'</span><span class="price">\' + product.special + \'</span></div>\';\r\n\t\t\t\t\t\t\t\t\t\t\t\t\t\t} else {\r\n\t\t\t\t\t\t\t\t\t\t\t\t\t\t\thtml += \'\t<div class="product-price"><span class="price">\' + product.price + \'</span></div>\';\r\n\t\t\t\t\t\t\t\t\t\t\t\t\t\t}\r\n\t\t\t\t\t\t\t\t\t\t\t\t\t}\r\n\t\t\t\t\t\t\t\t\t\t\t\t\thtml += \'<span style="clear:both"></span>\';\r\n\t\t\t\t\t\t\t\t\t\t\t\t\thtml += \'</a>\';\r\n\t\t\t\t\t\t\t\t\t\t\t\t\thtml += \'</li>\';\r\n\t\t\t\t\t\t\t\t\t\t\t\t\t$(\'.live-search ul\').append(html);\r\n\t\t\t\t\t\t\t\t\t\t\t\t});\r\n\t\t\t\t\t\t\t\t\t\t\t} else {\r\n\t\t\t\t\t\t\t\t\t\t\t\tvar html = \'\';\r\n\t\t\t\t\t\t\t\t\t\t\t\thtml += \'<li style="text-align: center;height:10px;">\';\r\n\t\t\t\t\t\t\t\t\t\t\t\thtml +=\tlive_search.text_no_matches;\r\n\t\t\t\t\t\t\t\t\t\t\t\thtml +=\t\'</li>\';\r\n\r\n\t\t\t\t\t\t\t\t\t\t\t\t$(\'.live-search ul\').html(html);\r\n\t\t\t\t\t\t\t\t\t\t\t}\r\n\t\t\t\t\t\t\t\t\t\t\t$(\'.live-search ul li\').css(\'height\',live_search.height);\r\n\t\t\t\t\t\t\t\t\t\t\t$(\'.live-search\').css(\'display\',\'block\');\r\n\t\t\t\t\t\t\t\t\t\t\treturn false;\r\n\t\t\t\t\t\t\t\t\t\t}\r\n\t\t\t\t\t\t\t\t\t});\r\n\t\t\t\t\t\t\t\t}\r\n\t\t\t\t\t\t\t},\r\n\t\t\t\t\t\t\t\'select\': function(product) {\r\n\t\t\t\t\t\t\t\t$(live_search.selector).val(product.name);\r\n\t\t\t\t\t\t\t}\r\n\t\t\t\t\t\t});\r\n\r\n\t\t\t\t\t\t$(document).bind( "mouseup touchend", function(e){\r\n\t\t\t\t\t\t  var container = $(\'.live-search\');\r\n\t\t\t\t\t\t  if (!container.is(e.target) && container.has(e.target).length === 0)\r\n\t\t\t\t\t\t  {\r\n\t\t\t\t\t\t    container.hide();\r\n\t\t\t\t\t\t  }\r\n\t\t\t\t\t\t});\r\n\t\t\t\t\t});\r\n\t\t\t\t//--></script>\r\n\t\t\t<?php endif;?>\r\n            ]]>\r\n            </add>\r\n        </operation>\r\n    </file>\r\n</modification>\r\n', '1', '2017-07-20 21:09:07');
INSERT INTO `oc_modification` (`modification_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES ('5', 'Newsletter - Sellegance', 'newsletter', 'everthemes.com', '2.1', '', '<?xml version="1.0" encoding="utf-8"?>\r\n<modification>\r\n\t<name>Newsletter - Sellegance</name>\r\n\t<code>newsletter</code>\r\n\t<version>2.1</version>\r\n\t<author>everthemes.com</author>\r\n\r\n\t<file path="admin/model/customer/customer.php">\r\n\t\t<operation>\r\n\t\t\t<search index="1"><![CDATA[\r\n\t\t\t\treturn $query->rows;\r\n\t\t\t]]></search>\r\n\t\t\t<add position="replace"><![CDATA[\r\n\t\t\t\tif ($this->config->get(\'newsletter_global_status\') && !empty($data[\'filter_newsletter\']) && isset($data[\'start\']) && $data[\'start\'] == 0) {\r\n\t\t\t\t\t$query1 = $this->db->query("SELECT * FROM " . DB_PREFIX . "newsletter");\r\n\t\t\t\t\t\r\n\t\t\t\t\treturn array_merge($query1->rows, $query->rows);\r\n\t\t\t\t} else {\r\n\t\t\t\t\treturn $query->rows;\r\n\t\t\t\t}\r\n\t\t\t]]></add>\r\n\t\t</operation>\r\n\t</file>\r\n\t\r\n\t<file path="admin/language/*/common/column_left.php">\r\n\t\t<operation error="skip">\r\n\t\t\t<search><![CDATA[\r\n\t\t\t// Text\r\n\t\t\t]]></search>\r\n\t\t\t<add position="after"><![CDATA[\r\n\t\t\t\t// Extra Tabs text\r\n\t\t\t\t$_[\'text_newsletter\'] = \'Newsletter Subscribers\';\r\n\t\t\t]]></add>\r\n\t\t</operation>\r\n\t</file>\r\n\r\n\t<file path="admin/controller/common/column_left.php">\r\n\t\t<operation error="skip">\r\n\t\t\t<search><![CDATA[\r\n\t\t\t\tif ($catalog) {\r\n\t\t\t]]></search>\r\n\t\t\t<add position="before" trim="true"><![CDATA[\r\n\t\t\t\tif ($this->user->hasPermission(\'access\', \'extension/newsletter\')) {\t\t\r\n\t\t\t\t\t$catalog[] = array(\r\n\t\t\t\t\t\t\'name\'\t   => $this->language->get(\'text_newsletter\'),\r\n\t\t\t\t\t\t\'href\'     => $this->url->link(\'extension/newsletter\', \'token=\' . $this->session->data[\'token\'], true),\r\n\t\t\t\t\t\t\'children\' => array()\t\t\r\n\t\t\t\t\t);\t\t\t\t\t\r\n\t\t\t\t}\r\n\t\t\t\t]]>\r\n\t\t\t</add>\r\n\t\t</operation>\r\n\t</file>\r\n\r\n\t<file path="admin/controller/marketing/contact.php">\r\n\t\t<operation>\r\n\t\t\t<search><![CDATA[\r\n\t\t\t\t$mail->setHtml($message);\r\n\t\t\t]]></search>\r\n\t\t\t<add position="before"><![CDATA[\r\n\t\t\t\tif ($this->config->get(\'newsletter_global_status\') && $this->request->post[\'to\'] == \'newsletter\') {\r\n\t\t\t\t\t$message1 = $message;\r\n\t\t\t\t\t$message = str_replace(\'{unsubscribe_link}\', HTTP_CATALOG . \'index.php?route=extension/module/newsletter/unsubscribe&id=\' . md5(\'newsletter\' . $email), $message1);\r\n\t\t\t\t}\r\n\t\t\t]]></add>\r\n\t\t</operation>\r\n\t</file>\r\n\r\n\t<file path="catalog/model/account/customer.php">\r\n\t\t<operation error="skip">\r\n\t\t\t<search><![CDATA[\r\n\t\t\t\tpublic function addCustomer($data) {\r\n\t\t\t]]></search>\r\n\t\t\t<add position="after"><![CDATA[\r\n\t\t\t\tif ($this->config->get(\'newsletter_global_status\') && !empty($data[\'email\'])) {\r\n\t\t\t\t\t$this->db->query("DELETE FROM " . DB_PREFIX . "newsletter WHERE email = \'" . $this->db->escape($data[\'email\']) . "\'");\r\n\t\t\t\t}\r\n\t\t\t]]></add>\r\n\t\t</operation>\r\n\t</file>\r\n\r\n</modification>', '1', '2017-07-20 21:09:10');
